        private static IList<Capital> capitals = null;
        private static Dictionary<string, Capital> dict = null;


        private static void Initialize()
        {
            capitals = JsonConvert.DeserializeObject<IList<Capital>>(json);
            dict = new Dictionary<string, Capital>(capitals.Count);

            foreach (Capital capital in capitals)
            {
                dict.Add(capital.country, capital);
            }
        }


        public static Capital GetCapital(string country)
        {
            if (dict == null)
                Initialize();

            Capital capital;
            dict.TryGetValue(country, out capital);
            return capital;
        }


        public static IList<Capital> GetCapitals()
        {
            if (capitals == null)
                Initialize();

            return capitals;
        }

